/**
 * @author Ahmed Rafsan Raqib
 * This is the main module where the Application is initiated.
 */
import "./App.css";
import DriverSearchForm from "./Components/DriverSearchForm";
import React from "react";
import ApplicationFooter from "./Components/ApplicationFooter";
import ListDrivers from "./Components/ListDrivers";
import { Routes, Route } from "react-router-dom";
import { useSelector } from "react-redux";

function App() {
  const [patientInfo, setPatientName] = React.useState(null);
  const [patientPickupAddress, setPatientPickupAddress] = React.useState(null);
  const [patientPickupTime, setPatientPickupTime] = React.useState(null);
  const [patientNotes, setPatientNotes] = React.useState(null);

  const optimumDriversList = useSelector(
    (patientData) => patientData.optimumDriversList
  );

  const patientPickupInfo = useSelector((patientData) => patientData);
  const driversList = [];

  //Method that sends the email to the driver
  const sendEmailToDrivers = (selectedDriverData) => {
    const email = createEmailContent(selectedDriverData);

    console.log(email);
  };

  //Method that creates the email
  const createEmailContent = (data) => {
    const driver = driversList.find(
      (selectedDriverData) => selectedDriverData.driverID === data.id
    );

    const heading =
      "======================\n" +
      "  Pickup Information\n" +
      "======================\n";
    const subject = "Patient Pickup for " + driver.name + "\n";
    const body =
      "Patient Name: " +
      patientPickupInfo.patientName +
      "\nPickup Address: " +
      patientPickupInfo.pickupAddress +
      "\nPickup Time: " +
      patientPickupInfo.pickupTime +
      "\nPickup Notes: " +
      patientPickupInfo.pickupNotes;

    return {
      emailId: driver.email,
      heading: heading,
      subject: subject,
      body: body,
    };
  };

  return (
    <div className="container">
      <div className="App">
        <Routes>
          <Route
            path="/"
            element={
              <DriverSearchForm
                label1={"Patient Name"}
                label1Placeholder={"Example, Firstname M Lastname.."}
                label1OnChange={this.state.setPatientName}
                label2={"Pickup Address"}
                label2Placeholder={"Apt no., Street, City, Zip, State"}
                label2OnChange={this.state.setPatientPickupAddress}
                label3={"Pickup Time"}
                label3Placeholder={"Example, 11:05 AM.."}
                label3OnChange={this.state.setPatientPickupTime}
                label4={"Pickup Notes"}
                label4Placeholder={"Patient Notes.."}
                label4OnChange={this.state.setPatientNotes}
              />
            }
            exact
          />
          <Route
            path="/listDrivers"
            element={
              <ListDrivers
                listToRender={optimumDriversList}
                initiateClickEvent={sendEmailToDrivers}
              />
            }
          />
          <Route component={Error}></Route>
        </Routes>
      </div>

      <div>
        <ApplicationFooter />
      </div>
    </div>
  );
}

export default App;
