import React from 'react'
import { useDispatch} from 'react-redux';
import {useNavigate}from 'react-router-dom';
//import determineLatLongFromAddress from '../Geolocation/locationServices';

export default function DriverSearchByLocation() {
    const [patientInfo, setPatientName] = React.useState(null);
    const [patientPickupAddress, setPatientPickupAddress] = React.useState(null);
    const [patientPickupTime, setPatientPickupTime] = React.useState(null);
    const [patientNotes, setPatientNotes] = React.useState(null);
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const handleSubmit = async (e) => {
      e.preventDefault();

      const {lat, lng} = await determineLatLongFromAddress(address);
      var optimumDriverList = [];
      driversList.forEach(function (driver) {
        const lat1 = driver.latitude;
        const lng1 = driver.longitude;

        var distance = determineDistanceBetweenDriverPatient(lat, lng, lat1, lng1);
        const driverData = {id: driver.driverID, driverName: driver.name, vehicleId: driver.vehicleId, 
          distanceFromPatient: distance};

        optimumDriverList.push(driverData);
      });

      optimumDriverList.sort(function(a,b){
        return a.distanceFromPatient - b.distanceFromPatient
      });

      displayDrivers = optimumDriverList;
        dispatch({
          type: 'SETPATIENTDATA',
          payload: {
            id: patientInfo,patientPickupAddress,patientPickupTime,patientNotes
          }
        }); 
        navigate("/listDrivers");
      };

  return (
    <div className='auth-form-div'>
        <form onSubmit={handleSubmit} className="driver-form">
            <label>Patient Name</label>
            <input onChange={(e) => setPatientName(e.target.value)} placeholder='Patient Name'></input>


            <label>Pickup Address</label>
            <input onChange={(e) => setPatientPickupAddress(e.target.value)} placeholder='Apt no., Street, City, Zip, State'></input>

            <label>Pickup Time</label>
            <input onChange={(e) => setPatientPickupTime(e.target.value)} placeholder='Pickup Time'></input>

            <label>Patient Notes</label>
            <input onChange={(e) => setPatientNotes(e.target.value)} placeholder='Patient Notes'></input>

            <button className='driver-button' type="submit">
              Drivers 
            </button>
        </form>
    </div>
  )
}
