/**
 * @author Ahmed Rafsan Raqib
 * This component renders the Driver Search form where the user inputs the
 * Driver name, pickup address, pickup time and any notes the patient might have left.
 * Once all the information is entered and the Search button is clicked the system
 * Calculates the nearest driver by comparing patient location with the Driver's current
 * Location and creates an Array with the nearest Drivers available. Redux Store gets
 * Updated with the information.
 */

import React from "react";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import determineLatLongFromAddress from "../Geolocation/locationServices";
import { determineDistanceBetweenDriverPatient } from "../Geolocation/locationServices";

export default function DriverSearchByLocation(props) {
  const [patientInfo, setPatientName] = React.useState(null);
  const [patientPickupAddress, setPatientPickupAddress] = React.useState(null);
  const [patientPickupTime, setPatientPickupTime] = React.useState(null);
  const [patientNotes, setPatientNotes] = React.useState(null);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { lat, lng } = await determineLatLongFromAddress(
      patientPickupAddress
    );
    var optimumDriverList = [];
    driversList.forEach(function (driver) {
      const lat1 = driver.latitude;
      const lng1 = driver.longitude;

      //Calculate distance between Driver and Patient
      var distance = determineDistanceBetweenDriverPatient(
        lat,
        lng,
        lat1,
        lng1
      );
      const driverData = {
        id: driver.driverID,
        driverName: driver.name,
        vehicleId: driver.vehicleId,
        distanceFromPatient: distance.toFixed(1),
      };

      optimumDriverList.push(driverData);
    });

    // Sorting list to display nearest Driver first
    optimumDriverList.sort(function (a, b) {
      return a.distanceFromPatient - b.distanceFromPatient;
    });

    // Sending data to redux store
    dispatch({
      type: "SETPATIENTDATA",
      payload: {
        id: patientInfo,
        patientPickupAddress,
        patientPickupTime,
        patientNotes,
        optimumDriverList,
      },
    });

    navigate("/listDrivers");
  };

  return (
    <div className="auth-form-div">
      <form onSubmit={handleSubmit} className="driver-form">
        <label>props.label1</label>
        <input
          onChange={(e) => setPatientName(e.target.value)}
          placeholder="Example, Firstname M Lastname.."
        ></input>

        <label>Pickup Address</label>
        <input
          onChange={(e) => setPatientPickupAddress(e.target.value)}
          placeholder="Apt no., Street, City, Zip, State"
        ></input>

        <label>Pickup Time</label>
        <input
          onChange={(e) => setPatientPickupTime(e.target.value)}
          placeholder="Example, 11:05 AM.."
        ></input>

        <label>Patient Notes</label>
        <input
          onChange={(e) => setPatientNotes(e.target.value)}
          placeholder="Patient Notes.."
        ></input>

        <button className="driver-button" type="submit">
          Drivers
        </button>
      </form>
    </div>
  );
}
